package com.Class7;

public class CompoundOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		a+=100;
		System.out.println(a);
		
		int b=100;
		b-=67;
		System.out.println(b);
		
		int cakePiece=11;
		cakePiece/=4;
		System.out.println(cakePiece);
		
		int cakePiece1=25;
		cakePiece1%=7;
		System.out.println(cakePiece1);
		
		
	
	}

}
