package com.class1;

public class DoubleComparison {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double a = 35.6655;

		double b = 35.6675;
		if (a > b) {
			System.out.println("Double value " + a + " is larger than " + b + " .");

		} else {

			System.out.println("Double value " + a + " is smaller than " + b + " .");
		}

	}

}
