package com.class2;

public class VariablesJava2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		double a = 30.25;
		double b = 12.50;
		

		double sum = a + b;
		double sub = a - b;
		double mult = a * b;
		double  div = a / b;
	
		
		System.out.println("the addition of 2 numbers "+a+" and "+b +" is "+sum);
		System.out.println("the subtruction of 2 numbers "+a+" and "+b +" is "+sub);
		System.out.println("the multiplation of 2 numbers "+a+" and "+b +" is "+mult);
		System.out.println("the division of 2 numbers "+a+" and "+b +" is "+div);
		
		double d=3.9;
		double sqr=d*d;
		
		System.out.println("The square of "+d +" is "+sqr);
		
		double width=5;
		double height=8;
		
		double perimeter=2*(width+height);
		
		
		double area=width*height;
		
		System.out.println("The perimeter of a rectangle with width "+width+" and height "+height+" is equal to "+perimeter+" and the area is "+area);
		
		
		
		

	}

}
